import { useForm } from 'react-hook-form'

function Registration() {

    const { register, handleSubmit, formState: { errors } } = useForm()
    const onSubmit = data => console.log(data)

    return (

        <form className='Form' onSubmit={handleSubmit(onSubmit)}>

            <label>User Name</label><br />
            <input {...register("UserName", {required: true, maxLength: 20})} /><br />
            {errors.UserName?.type === "required" && <div>User Name is required</div>}

            <label>Email</label><br />
            <input {...register("Email", {required: true})} /><br />
            {errors.Email?.type === "required" && <div>Email is required</div>}

            <label>Password</label><br />
            <input {...register("Password", {required: true, minLength: 8, maxLength: 20})} /><br />
            {errors.Pass?.type === "required" && <div>Password is required</div>}
            {errors.UserName?.type === "minLength" && <div>Password must be of atleast of 8 characters</div>}
            {errors.UserName?.type === "maxLength" && <div>Password must not be more than 20 characters</div>}

            <label>Confirm Password</label><br />
            <input {...register("Confirm Password", {required: true, minLength: 8, maxLength: 20})} /><br />

            <button>Register</button>

        </form>

    )
}


export default Registration
