import { useForm } from 'react-hook-form'
import './registration.css'

function Registration() {

    const { register, handleSubmit, formState: { errors } } = useForm()
    const onSubmit = data => console.log(data)

    return (
            <form  onSubmit={handleSubmit(onSubmit)}>

                <input placeholder='Enter your user name' {...register("UserName", { required: true, maxLength: 20 })} /><br />
                {errors.UserName?.type === "required" && <div>User Name is required</div>}
                <br />
                <input placeholder='Enter your email' {...register("Email", { required: true })} /><br />
                {errors.Email?.type === "required" && <div>Email is required</div>}

                <input placeholder='Enter your password' {...register("Password", { required: true, minLength: 8, maxLength: 20 })} /><br />
                {errors.Password?.type === "required" && <div>Password is required</div>}
                {errors.Password?.type === "minLength" && <div>Password must be of atleast of 8 characters</div>}
                {errors.Password?.type === "maxLength" && <div>Password must not be more than 20 characters</div>}

                <button>Register</button>

            </form>
    )
}


export default Registration
