import { useForm } from 'react-hook-form'
import { Link } from 'react-router-dom'
import Login from './Login'
import './registration.css'

function Registration() {

    const { register, handleSubmit, formState: { errors } } = useForm()
    const onSubmit = data => console.log(data)

    return (
        <div className='formContainer'>
            <h2>Registration for ChatApp</h2>
            <form onSubmit={handleSubmit(onSubmit)}>
                <input className='formfield' placeholder='Enter your Username' {...register("UserName", { required: true, maxLength: 20 })} /><br />
                {errors.UserName?.type === "required" && <div>User Name is required</div>}

                <input className='formfield' placeholder='Enter your Email' {...register("Email", { required: true })} /><br />
                {errors.Email?.type === "required" && <div>Email is required</div>}

                <input className='formfield' placeholder='Enter your Password' {...register("Password", { required: true, minLength: 8, maxLength: 20 })} /><br />
                {errors.Password?.type === "required" && <div>Password is required</div>}
                {errors.Password?.type === "minLength" && <div>Password must be of atleast of 8 characters</div>}
                {errors.Password?.type === "maxLength" && <div>Password must not be more than 20 characters</div>}

                <button className='formfield'>Register</button>
                <span>Already have an account <Link to={<Login />}>Login</Link></span>
            </form>
        </div>
    )
}


export default Registration
