import { useForm } from 'react-hook-form'

function Registration() {

    const { register, handleSubmit, formState: { errors } } = useForm()
    const onSubmit = data => console.log(data)

    return (
        <div className='Form'>
            <form onSubmit={handleSubmit(onSubmit)}>

                <label>User Name</label><br />
                <input {...register("UserName", { required: true, maxLength: 20 })} />
                {errors.UserName?.type === "required" && <p>User Name is required</p>}

                <label>Email</label><br />
                <input {...register("Email", { required: true })} /><br />
                {errors.Email?.type === "required" && <p>Email is required</p>}

                <label>Password</label><br />
                <input {...register("Password", { required: true, minLength: 8, maxLength: 20 })} /><br />
                {errors.Password?.type === "required" && <p>Password is required</p>}
                {errors.Password?.type === "minLength" && <p>Password must be of atleast of 8 characters</p>}
                {errors.Password?.type === "maxLength" && <p>Password must not be more than 20 characters</p>}

                <button>Register</button>

            </form>
        </div>
    )
}


export default Registration
